package vo;

public class DeptVO {

		int deptNo;
		String dName;
		String loc;
		
		public DeptVO() {
			// TODO Auto-generated constructor stub
		}
		
		public void setDeptNo(int deptNo) {
			this.deptNo = deptNo;
		}
		public void setdName(String dName) {
			this.dName = dName;
		}
		public void setLoc(String loc) {
			this.loc = loc;
		}
		public int getDeptNo() {
			return deptNo;
		}
		public String getdName() {
			return dName;
		}
		public String getLoc() {
			return loc;
		}
	
}
